function reddUp%% closes screens, shuts down eyelink, clears working memory% % by Martin Rolfsglobal setting% re-enable keyboardListenChar(1);% get eyelink data file on subject computerif setting.TEST==0, status = Eyelink('ReceiveFile');%, edffilename, 'Data', 1); %,edffilename, edffilename, 'Data/');else status=0; endif status == 0    fprintf(1,'\n\nFile transfer was cancelled\n\n');elseif status < 0    fprintf(1,'\n\nError occurred during file transfer\n\n');else    fprintf(1,'\n\nFile has been transferred (%i Bytes)\n\n',status)end%Eyelink runterfahrenif setting.TEST==0    Eyelink('closefile');    WaitSecs(2.0); % Zeit fur Tracker, alles fertigzubekommen    Eyelink('shutdown');    WaitSecs(2.0);endFlushEvents('keyDown');% Screens schliessenShowCursor;% Screen(visual.main,'Resolution', scr.oldRes);Screen('CloseAll');% Speicher aufraumenclear mex;clear fun;home;